import React, {useState, useEffect} from "react";
import "../assets/css/ProductDetail.css";

import baseUrl from "../baseUrl";

import { useParams } from "react-router-dom";

const ProductDetail = () => {
  const { productId } = useParams();
  const [productDetail, SetProductDetail] = useState();
  const [error, setError] = useState(false)

  useEffect(()=>{
    getProductDetail()
  },[])

  const getProductDetail = async() => {
    const response  = await fetch (`${baseUrl}/api/product/detail/${productId}/`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json'
      }
    })

    if(response.status === 200){
      const data = await response.json()
      SetProductDetail(data)
    }
    else{
      setError(true)
    }
  };
  // console.log("PRODUCT DETAIL :", productDetail)
  const loading = () => {
    return (
      <div className="w-16 h-16 border-4 border-dashed rounded-full animate-spin dark:border-primaryBtn">
      </div>
    )
  }
  return (
    <div>
      {!productDetail ? (<loading />) : (
        <button type="button" className="bg-indigo-500 my-" disabled>
        <svg className="animate-spin h-5 w-5 mr-3 " viewBox="0 0 24 24">

        </svg>
        Processing...
      </button>
      )}

    </div>
  );
};

export default ProductDetail;
