import { Link } from "react-router-dom";

import Card from "../Card";


const CardBouquet = () => {

  const [products, setProducts] = useState([])

  useEffect(()=>{
    getProduct()
  }, [])

  const getProduct = async() => {
    const response = await fetch(`${baseUrl}/api/product/bouquet/`,{
      method: 'GET'
    })
    const data = await response.json()
    setProducts(data)
  }

  return (
    <div className="bg-white">
      <div className="max-w-2xl mx-auto py-16 px-4 sm:py-24 sm:px-6 lg:max-w-7xl lg:px-8">
        <h1 className="mb-10 text-4xl font-bold text-center leading-none tracking-tighter text-neutral-600 md:text-7xl lg:text-5xl">
          BOUQUET
        </h1>

        <div className="mt-6 grid grid-cols-1 gap-y-10 gap-x-6 sm:grid-cols-2 lg:grid-cols-4 xl:gap-x-8">

        </div>
      </div>
      <Link to="/bouteques">
      <p  style={{marginTop: "-35px", textDecoration: "underline"}} className="text-center transition duration-500 ease-in-out text-primaryBtn mb-10 smooth hover:text-lg">explore</p>
      </Link>
    </div>
  );
};

export default CardBouquet;
