import React, { useState, useEffect } from "react";
import InfiniteScroll from "react-infinite-scroller";

import Card from "../components/Card";

import baseUrl from "../baseUrl";

const Flowers = () => {
  const [products, setProducts] = useState([]);
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);

  useEffect(() => {
    getProduct();
  }, []);

  const getProduct = async () => {
    const response = await fetch(
      `${baseUrl}/api/product/flowers/?page=${page}`,
      {
        method: "GET",
      }
    );
    const data = await response.json();
    const productData = await data.results;
    setProducts({ ...products, ...productData });
    setPage(page + 1);

    if (data.next === null) {
      setHasMore(false);
    }
  };

  // console.log("PRODUCTS : ", products)
  return (
    <div className="bg-white" id="flowers">
      <div className="max-w-2xl mx-auto py-16 px-4 sm:py-24 sm:px-6 lg:max-w-7xl lg:px-8">
        <h1 className="mb-10 text-4xl font-bold text-center leading-none tracking-tighter text-neutral-600 md:text-7xl lg:text-5xl">
          FLOWER
        </h1>

        <div className="mt-6 grid grid-cols-1 gap-y-10 gap-x-6 sm:grid-cols-2 lg:grid-cols-4 xl:gap-x-8">
          <InfiniteScroll
            dataLength={data.length}
            next={fetchImages}
            hasMore={hasMore}
            loader={<Spiner />}
            endMessage={
              <p style={{ textAlign: "center" }}>
                <b>Yay! You have seen it all</b>
              </p>
            }
          >
            {products.map((product, index) => (
              <Card
                key={index}
                id={product.id}
                title={product.title}
                primaryImg={product.primaryImg}
              />
            ))}

          </InfiniteScroll>
        </div>
      </div>
    </div>
  );
};

export default Flowers;
